    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked

        if(Pattern.matches("[0-9]+",jTextField1.getText())==false|| jTextField1.getText().length()>13 )
{//System.out.println("mm");
    
     JOptionPane.showMessageDialog(null,
                  "check your ID again:-(",
                  "ERROR MANAGER (^_^)",
                  JOptionPane.ERROR_MESSAGE);
    return;}
        
int n1= Integer.parseInt(jTextField1.getText());

if(Pattern.matches("[0-9]+",jTextField1.getText())==false|| jTextField1.getText().length()>5 )
{//System.out.println("mm");
                
    return;}




int errflag=0;     
concourier.createconnection();
           
        ResultSet rs= concourier.getData("select * from dbcou where coid="+n1+"");
      try{
        
      while(rs.next())
      { errflag=1;
      //  int t= Integer.parseInt(rs.getString(1))+1;
          jTextField2.setText(rs.getString(2));
          jTextArea1.setText(rs.getString(3));
          jTextArea2.setText(rs.getString(4));
          jTextField3.setText(rs.getString(5));
          jTextField4.setText(rs.getString(6));
          jTextField5.setText(rs.getString(7));
          jComboBox1.setSelectedItem(rs.getString(8));
     //     System.out.println(jComboBox1.getSelectedItem());
      }
       
          if(errflag==0)
          { System.out.println("Not foound!!");
           JOptionPane.showMessageDialog(null,
                  "NO courier  Found :-(",
                  "ERROR MANAGER (^_^)",
                  JOptionPane.ERROR_MESSAGE);
          }
       
      }
      
      catch(Exception e)
      {
      }
        
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1MouseClicked

