    private void jButton3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton3MouseClicked
   concourier.createconnection();

String nos1=jTextField1.getText();
int no1=Integer.parseInt(nos1);
 
String comboo=(String) jComboBox1.getSelectedItem();

//String statem="insert into dbcou values("+no1+","+nos2+",'"+addto+"','"+addfrom+"','"+wt+"','"+amt+"','"+dat+"',"+comboo+")";

 String statem="update dbcou set costatus = '"+comboo+"' where coid ="+no1;
 concourier.execute(statem); 

    }//GEN-LAST:event_jButton3MouseClicked

